mapper.select:
SELECT B.BRAND_NAME, A.PICKUP, A.PICKUP_DEST, A.DEST, A.ITEM, A.REPLACE_ITEM, A.ORDER_TIME, A.DRIVER_NUM, A.PRICE, A.ORDER_NUM, A.LOGIN_ID, IF(A.DRIVER_NUM = 0, TRUE,FALSE) AS isBatch FROM TB_ORDER A LEFT JOIN TB_USER B ON A.LOGIN_ID = B.LOGIN_ID WHERE 1 = 1
AND A.DRIVER_NUM = #{driverNum}
AND A.LOGIN_ID = #{loginId}
ORDER BY A.ORDER_TIME, isBatch DESC

mapper.update:
UPDATE TB_ORDER SET
PICKUP = 'N'
PICKUP = 'Y'
,DRIVER_NUM = #{driverNum}
WHERE ORDER_NUM = #{orderNum}
